mapper.select:
select
t_testbusiness_ui.*,sp.project_name
from t_testbusiness_ui inner join  sys_project sp on t_testbusiness_ui .project_id = sp.id
where 1=1

    and t_testbusiness_ui.id=#{id,jdbcType=BIGINT}


    and project_id = #{projectId,jdbcType=BIGINT}


    and t_testbusiness_ui.`name`=#{name,jdbcType=VARCHAR}


    and close_browser=#{closeBrowser,jdbcType=BIGINT}


    and timout_time=#{timoutTime,jdbcType=BIGINT}


    and fail_continue=#{failContinue,jdbcType=INTEGER}


    and remark=#{remark,jdbcType=VARCHAR}


    and create_by=#{createBy,jdbcType=VARCHAR}


    and update_by=#{updateBy,jdbcType=VARCHAR}


    and create_time=#{createTime,jdbcType=TIMESTAMP}


    and update_time=#{updateTime,jdbcType=TIMESTAMP}
