mapper.select:
select
t_testcase_ui.*,s.project_name
from t_testcase_ui left join sys_project s on t_testcase_ui.project_id = s.id
where 1=1

    and t_testcase_ui.id=#{id,jdbcType=BIGINT}


    and t_testcase_ui.`name` like  concat('%',#{name} ,'%')


    and project_id= #{projectId,jdbcType=BIGINT}


    and case_type=#{caseType,jdbcType=BIGINT}


    and remark like  concat('%',#{remark} ,'%')


    and create_by=#{createBy,jdbcType=VARCHAR}


    and update_by=#{updateBy,jdbcType=VARCHAR}


    and create_time=#{createTime,jdbcType=TIMESTAMP}


    and update_time=#{updateTime,jdbcType=TIMESTAMP}
